import { useEffect, useState } from 'react';
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from "../hooks/useAuthContext";

// components
import CalendarNav from '../components/CalendarNav';
import EventsDetails from '../components/EventsDetails';
import WorkoutForm from '../components/WorkoutForm';
import Calendar from '../components/Calendar';
import EventForm from '../components/CalendarForm';

const Home = () => {
  const { workouts, dispatch } = useWorkoutsContext();
  const { user } = useAuthContext();
  const [startDate, setStartDate] = useState("2024-10-04"); // Initialize startDate here

  useEffect(() => {
    const fetchWorkouts = async () => {
      const response = await fetch('/api/workouts', {
        headers: { 'Authorization': `Bearer ${user.token}` },
      });
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_WORKOUTS', payload: json });
      }
    };

    if (user) {
      fetchWorkouts();
    }
  }, [dispatch, user]);

  return (
    <div className="home">
      <div>
        <CalendarNav startDate={startDate} setStartDate={setStartDate} />
      </div>
      <div className='calendar'>
        <Calendar startDate={startDate} />
      </div>
      <EventForm />
      <div className="workouts">
        {workouts && workouts.map((workout) => (
          <EventsDetails key={workout._id} workout={workout} />
        ))}
      </div>
    </div>
  );
};

export default Home;
