import React, { useEffect, useState } from "react";
import { useAuthContext } from "../hooks/useAuthContext";

const Account = () => {
  const { user } = useAuthContext();
  const [classroomData, setClassroomData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [teacherCode, setTeacherCode] = useState("");
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState("");

  // Fetch classroom data for teacher or student
  useEffect(() => {
    if (user) {
      const fetchClassroom = async () => {
        try {
          let endpoint = "";
          if (user.role === "teacher") {
            endpoint = `/api/classes/by-email?email=${user.email}`;
          } else if (user.role === "student" && user.classroomCode) {
            endpoint = `/api/classes/by-code?code=${user.classroomCode}`;
          }

          if (endpoint) {
            const response = await fetch(endpoint, {
              method: "GET",
              headers: {
                Authorization: `Bearer ${user.token}`,
              },
            });

            const data = await response.json();
            if (response.ok) {
              setClassroomData(data);
            } else {
              console.log("No classroom data found");
            }
          }
        } catch (error) {
          console.error("Error fetching classroom:", error);
        } finally {
          setLoading(false);
        }
      };

      fetchClassroom();
    }
  }, [user]);

  // Handle teacher code input submission for students
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess("");

    try {
      const response = await fetch(`/api/classes/validate-code?code=${teacherCode}`, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        const patchResponse = await fetch(`/api/user/update-classroom`, {
          method: "PATCH",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${user.token}`,
          },
          body: JSON.stringify({ classroomCode: teacherCode }),
        });

        if (patchResponse.ok) {
          setClassroomData(data);
          setSuccess("Classroom successfully added!");
        } else {
          const patchError = await patchResponse.json();
          setError(patchError.error || "Failed to update classroom.");
        }
      } else {
        setError("Invalid teacher code. Please try again.");
      }
    } catch (err) {
      console.error("Error validating code:", err);
      setError("An error occurred. Please try again.");
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <div>Please log in to view your account details.</div>;
  }

  return (
    <div className="account">
      <h1>Account Info</h1>
      <p>Account Type: {user.role}</p>
      <p>Name: {user.name}</p>
      <p>Email: {user.email}</p>
      
      {user.role === "teacher" && (
        <>
          {classroomData ? (
            <>
              <p>Class Name: {classroomData.classroomName}</p>
              <p>Class Code: {classroomData.code}</p>
            </>
          ) : (
            <p>No classroom associated with this email.</p>
          )}
        </>
      )}

      {user.role === "student" && (
        <>
          {!classroomData && (
            <form onSubmit={handleSubmit}>
              <label>
                Enter Teacher's Code:
                <input
                  type="text"
                  value={teacherCode}
                  onChange={(e) => setTeacherCode(e.target.value)}
                  required
                />
              </label>
              <button type="submit">Submit</button>
            </form>
          )}
          {error && <p style={{ color: "red" }}>{error}</p>}
          {success && <p style={{ color: "green" }}>{success}</p>}
          {classroomData ? (
            <>
              <p>Class Name: {classroomData.classroomName}</p>
              <p>Class Code: {classroomData.code}</p>
              <p>Teacher's Email: {classroomData.email}</p>
            </>
          ) : (
            <p>No classroom associated yet.</p>
          )}
        </>
      )}
    </div>
  );
};

export default Account;
