import { useState } from 'react'
import { Link } from 'react-router-dom'

const HamburgerMenu = ({ user }) => {
  const [menuOpen, setMenuOpen] = useState(false)

  const toggleMenu = () => {
    setMenuOpen(!menuOpen)
  }

  return (
    <div className="hamburger-container">
      {/* Hamburger button to open/close menu */}
      {!menuOpen && (
        <div className="hamburger" onClick={toggleMenu}>
          <span className="line"></span>
          <span className="line"></span>
          <span className="line"></span>
        </div>
      )}

      {menuOpen && (
        <div className="hamburger-menu">
          {/* Hamburger button inside the menu to close it */}
          <div className="hamburger close-button" onClick={toggleMenu}>
            <span className="line"></span>
            <span className="line"></span>
            <span className="line"></span>
          </div>

          {/* Menu content */}
          <div className="menu-content">
            {user && (
              <>
                <span>{user.email}</span>
                {/* Placeholder links for account and calendar */}
                {/* <Link to="/account">Account</Link> */}
                {/* <Link to="/calendar">Calendar</Link> */}
              </>
            )}
            {!user && (
              <>
                <Link to="/login">Login</Link>
                <Link to="/signup">Signup</Link>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

export default HamburgerMenu
