import React from 'react';
import { DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; 
import { faTrash, faEdit } from '@fortawesome/free-solid-svg-icons';

const Calendar = ({ startDate, events, onDeleteEvent, onEditEvent }) => {
  console.log("Events in Calendar:", events); 

  // Ensure text is just a string, and the icons are rendered elsewhere
  const formattedEvents = events.map(event => ({
      id: event.id,
      text: event.text,  // Ensure this is a string, not a JSX element
      start: new Date(event.start).toISOString(),
      end: new Date(event.end).toISOString(),
      backColor: event.backColor || "#ffffff",
      participants: event.participants || 0,
  }));

  const config = {
      viewType: "Week",
      durationBarVisible: false,
      startDate: startDate,
      events: formattedEvents,
      onEventClick: (args) => {
        // Handle event click to bring up edit or delete options
        const eventId = args.e.id(); // Access event id from clicked event

        // You can display a modal or dropdown here for editing/deleting
        console.log(`Clicked event with ID: ${eventId}`);
      }
  };

  return (
      <div>
          <DayPilotCalendar {...config} />
          <div>
            {formattedEvents.map(event => (
              <div key={event.id} className="event-icons">
                <span>{event.text}</span> {/* Display the event text */}
                <FontAwesomeIcon 
                  icon={faEdit} 
                  onClick={() => onEditEvent(event.id, event)} 
                  style={{ cursor: 'pointer', marginLeft: '5px' }} 
                />
                <FontAwesomeIcon 
                  icon={faTrash} 
                  onClick={() => onDeleteEvent(event.id)} 
                  style={{ cursor: 'pointer', marginLeft: '5px' }} 
                />
              </div>
            ))}
          </div>
      </div>
  );
};

export default Calendar;
