import { useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from '../hooks/useAuthContext';

const EventForm = () => {
  const { dispatch } = useWorkoutsContext();
  const { user } = useAuthContext();

  const [text, setText] = useState('');
  const [startDate, setStartDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endDate, setEndDate] = useState('');
  const [endTime, setEndTime] = useState('');
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
        setError('You must be logged in');
        return;
    }

    if (!startDate || !startTime || !endDate || !endTime) {
        setError('Please fill in all date and time fields');
        return;
    }

    // Combine date and time into a single datetime string
    const start = new Date(`${startDate}T${startTime}`);
    const end = new Date(`${endDate}T${endTime}`);

    // Check if the dates are valid
    if (isNaN(start.getTime()) || isNaN(end.getTime())) {
        setError('Invalid date or time provided');
        return;
    }

    const event = { 
        text,
        start: start.toISOString(),
        end: end.toISOString(),
        user_id: user.id // Ensure this matches the backend
    };

    const response = await fetch('/api/events', {
        method: 'POST',
        body: JSON.stringify(event),
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${user.token}`,
        },
    });

    const json = await response.json();

    if (!response.ok) {
        setError(json.error);
        setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
        setText('');
        setStartDate('');
        setStartTime('');
        setEndDate('');
        setEndTime('');
        setError(null);
        setEmptyFields([]);
        console.log("Event dispatched:", json);
        dispatch({ type: 'CREATE_EVENT', payload: { ...json, id: json._id } });
    }
};

  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Add a New Event</h3>

      <label>Event Title:</label>
      <input 
        type="text"
        onChange={(e) => setText(e.target.value)}
        value={text}
        className={emptyFields.includes('text') ? 'error' : ''}
      />

      <label>Date:</label>
      <input 
        type="date"
        onChange={(e) => setStartDate(e.target.value)}
        value={startDate}
        className={emptyFields.includes('startDate') ? 'error' : ''}
      />

      <label>Start Time:</label>
      <input 
        type="time"
        onChange={(e) => setStartTime(e.target.value)}
        value={startTime}
        className={emptyFields.includes('startTime') ? 'error' : ''}
      />

      <label>End Time:</label>
      <input 
        type="time"
        onChange={(e) => setEndTime(e.target.value)}
        value={endTime}
        className={emptyFields.includes('endTime') ? 'error' : ''}
      />

      <button>Add Event</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default EventForm;
