import React from 'react';
import { DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons';

const Calendar = ({ startDate, events, onDeleteEvent }) => {
  const formattedEvents = events.map(event => ({
    id: event.id,
    text: event.text, // The text for the event
    start: new Date(event.start).toISOString(),
    end: new Date(event.end).toISOString(),
    backColor: event.backColor || "#ffffff",
  }));

  const config = {
    viewType: "Week",
    durationBarVisible: false,
    startDate: startDate,
    events: formattedEvents,
    eventDeleteHandling: "Update", // Handles the event update logic
    onBeforeEventRender: (args) => {
      // Customize event content to include trash icon
      args.data.html = `
        <div style="display: flex; justify-content: space-between; align-items: center;">
          <span>${args.data.text}</span>
          <span class="trash-icon" data-id="${args.data.id}">
            <i class="fas fa-trash-alt" style="cursor: pointer; color: red;"></i>
          </span>
        </div>`;
    },
    onEventClick: async (args) => {
      const trashIcon = document.querySelector(`.trash-icon[data-id="${args.e.id}"]`);
      if (trashIcon) {
        trashIcon.addEventListener("click", async (event) => {
          event.stopPropagation(); // Prevent calendar click
          const isConfirmed = window.confirm(`Are you sure you want to delete the event "${args.e.data.text}"?`);
          if (isConfirmed) {
            await onDeleteEvent(args.e.data.id); // Call delete event function
          }
        });
      }
    }
  };

  return (
    <div>
      <DayPilotCalendar {...config} />
    </div>
  );
};

export default Calendar;
