import React, { useEffect, useState } from 'react';
import { useAuthContext } from "../hooks/useAuthContext";

const Account = () => {
    const { user } = useAuthContext();
    const [userInfo, setUserInfo] = useState(null);

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const response = await fetch(`/api/users/${user.id}`); // Replace with your endpoint
                const data = await response.json();
                setUserInfo(data);
            } catch (error) {
                console.error("Error fetching user info:", error);
            }
        };

        if (user) {
            fetchUserInfo();
        }
    }, [user]);

    if (!user || !userInfo) {
        return <div>Loading...</div>;
    }

    return (
        <div className='account'>
            <h1>Account Info</h1>
            <p>Account Type: {user.role}</p>
            <p>Name: {userInfo.name}</p>
            <p>Email: {user.email}</p>
            {user.role === 'student' && <p>Teacher's Name: {userInfo.teacherName}</p>}
            <p>Class Name: {userInfo.className}</p>
            <p>Class Code: {userInfo.classCode}</p>
        </div>
    );
};

export default Account;
