import { useState, useEffect } from "react";
import { useEventsContext } from "../hooks/useEventsContext";
import { useAuthContext } from '../hooks/useAuthContext';
import { useClassContext } from '../hooks/useClassContext'; // Import the context

const EventForm = () => {
  const { dispatch } = useEventsContext();
  const { user } = useAuthContext();
  const { classroom } = useClassContext();  // Get the classroom context

  const [text, setText] = useState('');
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [color, setColor] = useState('');
  const [type, setType] = useState('');
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  let typeOptions = ['Homework', 'Test', 'Document', 'Other'];

  // Initialize classroom state from the context
  const [classroomCode, setClassroomCode] = useState(user ? classroom.code = user.code : '');
  
  useEffect(() => {
    if (classroom) {
      setClassroomCode(classroom.code);  // Set the classroom code when the context is available
      console.log("Classroom Code: ", classroom.code)
    }
  }, [classroom]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError('You must be logged in');
      return;
    }

    if (!date || !startTime || !endTime || !type) {
      setError('Please fill in all fields');
      return;
    }

    if (!classroomCode) {
      setError('Must create class before making an event');
      return;
    }

    const start = new Date(`${date}T${startTime}Z`);
    const end = new Date(`${date}T${endTime}Z`);

    if (isNaN(start.getTime()) || isNaN(end.getTime())) {
      setError('Invalid date or time provided');
      return;
    }

    const event = { 
      text,
      color,
      type,
      start: start.toISOString(),
      end: end.toISOString(),
      classroom: classroomCode  // Use the classroom code from context
    };

    try {
      const response = await fetch('/api/events', {
        method: 'POST',
        body: JSON.stringify(event),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`,
        },
      });

      if (!response.ok) {
        const json = await response.json();
        setError(json.error || 'Something went wrong');
        setEmptyFields(json.emptyFields || []);
      } else {
        const json = await response.json();
        setText('');
        setDate('');
        setStartTime('');
        setEndTime('');
        setColor('');
        setType('');
        setError(null);
        setEmptyFields([]);
        dispatch({ type: 'CREATE_EVENT', payload: { ...json, id: json._id } });

        window.location.reload();
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setError('An error occurred while creating the event.');
    }
  };

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const colorOptions = [
    'Black', 'Grey', 'Red', 'OrangeRed', 'MediumVioletRed',
    'Purple', 'BlueViolet', 'RoyalBlue', 'DarkBlue', 'ForestGreen'
  ];

  return (
    <>
      <button onClick={openModal} className="add-event-button">Add Event</button>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <form className="create" onSubmit={handleSubmit}>
              <h3>Add a New Event</h3>

              <label>Event Title:</label>
              <input
                type="text"
                onChange={(e) => setText(e.target.value)}
                value={text}
                className={emptyFields.includes('text') ? 'error' : ''}
              />

              <label>Event Type:</label>
              <div className="event-types">
                {typeOptions.map(typeOption => (
                  <button
                    key={typeOption}
                    type="button"
                    className="event-type-button"
                    onClick={() => setType(typeOption)}
                    style={{
                      background: type === typeOption ? 'var(--primary)' : '#fff',
                      color: type === typeOption ? '#fff' : 'var(--primary)',
                      border: '2px solid var(--primary)',
                      padding: '6px 10px',
                      borderRadius: '4px',
                      fontFamily: 'Poppins',
                      cursor: 'pointer',
                      fontSize: '1em',
                      margin: '2px',
                      position: 'relative'
                    }}
                  >
                    {typeOption}
                  </button>
                ))}
              </div>

              <label>Event Color:</label>
              <div className="color-picker">
                {colorOptions.map(colorOption => (
                  <div
                    key={colorOption}
                    className={`color-circle ${color === colorOption ? 'selected' : ''}`}
                    style={{ backgroundColor: colorOption }}
                    onClick={() => setColor(colorOption)}
                  />
                ))}
              </div>

              <label>Date:</label>
              <input
                type="date"
                onChange={(e) => setDate(e.target.value)}
                value={date}
                className={emptyFields.includes('date') ? 'error' : ''}
              />

              <label>Start Time:</label>
              <input
                type="time"
                onChange={(e) => setStartTime(e.target.value)}
                value={startTime}
                className={emptyFields.includes('startTime') ? 'error' : ''}
              />

              <label>End Time:</label>
              <input
                type="time"
                onChange={(e) => setEndTime(e.target.value)}
                value={endTime}
                className={emptyFields.includes('endTime') ? 'error' : ''}
              />

              <input 
                type="hidden"
                value={classroomCode}
              />

              <button type="submit" className="submit">Add Event</button>
              {error && <div className="error">{error}</div>}

              <button type="button" onClick={closeModal} className="close-modal">
                Close
              </button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default EventForm;
