import { React, useState } from 'react';
import { DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import { useAuthContext } from '../hooks/useAuthContext';

const Calendar = ({ startDate, events, onDeleteEvent, onEditEvent }) => {
  const { user } = useAuthContext();

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [isParentCodeVerified, setIsParentCodeVerified] = useState(false);  // Track if the parent code is verified
  const [enteredCode, setEnteredCode] = useState('');  // Store entered code

  const openModal = (event) => {
    setSelectedEvent(event);
    setIsModalOpen(true);
  };

  const closeModal = () => setIsModalOpen(false);

  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  const openEditModal = (event) => {
    setSelectedEvent(event);
    setIsEditModalOpen(true);
  };

  const closeEditModal = () => setIsEditModalOpen(false);

  const [text, setText] = useState('');
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [color, setColor] = useState('');
  const [type, setType] = useState('');
  const [classroom, setClassroom] = useState('');
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);
  const typeOptions = ['Homework', 'Test', 'Document', 'Other'];

  const colorOptions = [
    'Black', 'Grey', 'Red', 'OrangeRed', 'MediumVioletRed',
    'Purple', 'BlueViolet', 'RoyalBlue', 'DarkBlue', 'ForestGreen'
  ];

  function separateDateTime(dateTime) {
    const parts = dateTime.split('T');
    const date = parts[0];
    const time = parts[1];
    return { date, time };
  }

  const formattedEvents = events.map(event => ({
    id: event.id,
    text: event.text,
    start: new Date(event.start).toISOString(),
    end: new Date(event.end).toISOString(),
    backColor: event.backColor,
    participants: event.participants || 0,
    type: event.type,
    classroom: event.classroom
  }));

  const config = {
    viewType: "Week",
    durationBarVisible: false,
    startDate: startDate,
    events: formattedEvents,
  
    onBeforeEventRender: args => {
      args.data.backColor = args.data.backColor || "#ffffff";
  
      // Add green checkmark for verified parent code if the user is a student
      if (user && user.role === 'student') {
        if (isParentCodeVerified) {
          args.data.html = `
            <div style="display: flex; flex-direction: column; justify-content: space-between; height: 100%;">
              <div>${args.data.text}</div>
              <div style="color: green;">‚úîÔ∏è</div>
            </div>`;
        } else {
          // Use a span with a class and event listener instead of onclick inline handler
          args.data.html = `
            <div style="display: flex; flex-direction: column; justify-content: space-between; height: 100%;">
              <div>${args.data.text}</div>
              <div class="verify-parent-code" style="cursor: pointer; color: red;">
                ‚úîÔ∏è
              </div>
            </div>`;
        }
      } else if (user && user.role !== 'student') {
        args.data.html = `
          <div style="display: flex; flex-direction: column; justify-content: space-between; height: 100%;">
            <div>${args.data.text}</div>
            <div style="display: flex; justify-content: center; gap: 10px; margin-top: 5px;">
              ${onEditEvent ? '<span class="edit-icon" style="cursor: pointer;">‚úèÔ∏è</span>' : ''}
              ${onDeleteEvent ? '<span class="delete-icon" style="cursor: pointer;">üóëÔ∏è</span>' : ''}
            </div>
          </div>`;
      } else {
        args.data.html = `
          <div style="display: flex; flex-direction: column; justify-content: space-between; height: 100%;">
            <div>${args.data.text}</div>
            <div style="margin-top: 5px;"></div>
          </div>`;
      }
    },
  
    onEventClick: args => {
      const target = args.originalEvent.target;

      // Check if the clicked target is the checkmark (parent code verification)
      if (target.classList.contains('verify-parent-code') && !isParentCodeVerified) {
        // Open the parent code verification modal
        openParentCodeModal();
        return; // Prevent event details modal from opening
      }

      // Handle other event clicks (Edit/Delete or Open Event Details)
      if (onDeleteEvent || onEditEvent) {
        const eventId = args.e.id();

        if (onDeleteEvent && target.classList.contains("delete-icon")) {
          onDeleteEvent(eventId);
        } else if (onEditEvent && target.classList.contains("edit-icon")) {
          const event = formattedEvents.find(event => event.id === eventId);
    
          const { date: eventStartDate, time: eventStartTime } = separateDateTime(event.start.toString());
          const { date: eventEndDate, time: eventEndTime } = separateDateTime(event.end.toString());
          setText(event.text);
          setType(event.type);
          setColor(event.backColor);
          setDate(eventStartDate);
          setStartTime(eventStartTime);
          setEndTime(eventEndTime);
          setClassroom(event.classroom);
    
          openEditModal(event);
        } else {
          const event = formattedEvents.find(event => event.id === eventId);
          openModal(event);
        }
      }
    }
  };

  const openParentCodeModal = () => {
    // Open the parent code modal to enter the code
    setIsModalOpen(true);
  };

  const closeParentCodeModal = () => {
    setIsModalOpen(false);
  };

  const verifyParentCode = () => {
    if (enteredCode === user.parentCode) { // Replace with your actual parent code logic
      setIsParentCodeVerified(true);
      closeParentCodeModal(); // Close the modal once verified
    } else {
      setError('Invalid parent code');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError('You must be logged in');
      return;
    }

    if (!date || !startTime || !endTime || !type) {
      setError('Please fill in all fields');
      return;
    }

    const start = new Date(`${date}T${startTime}Z`);
    const end = new Date(`${date}T${endTime}Z`);

    if (isNaN(start.getTime()) || isNaN(end.getTime())) {
      setError('Invalid date or time provided');
      return;
    }

    const event = {
      text,
      color,
      type,
      start: start.toISOString(),
      end: end.toISOString(),
      classroom
    };

    onEditEvent(selectedEvent.id, event);
  };

  return (
    <div>
      <DayPilotCalendar {...config} />

      {/* Parent Code Verification Modal */}
      {isModalOpen && !isParentCodeVerified && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h1>Verify Parent Code</h1>
            <input
              type="text"
              placeholder="Enter Parent Code"
              value={enteredCode}
              onChange={(e) => setEnteredCode(e.target.value)}
            />
            {error && <div className="error">{error}</div>}
            <button type="button" onClick={verifyParentCode}>Verify</button>
            <button type="button" onClick={closeParentCodeModal}>Cancel</button>
          </div>
        </div>
      )}

      {isEditModalOpen && selectedEvent && (
        <div className="modal-overlay">
          <div className="modal-content">
            <form className="edit-event" onSubmit={handleSubmit}>
              <h3>Edit Event</h3>
              <label>Text:</label>
              <input type="text" value={text} onChange={(e) => setText(e.target.value)} />
              <label>Type:</label>
              <select value={type} onChange={(e) => setType(e.target.value)}>
                {typeOptions.map(option => <option key={option} value={option}>{option}</option>)}
              </select>
              <label>Classroom:</label>
              <input type="text" value={classroom} onChange={(e) => setClassroom(e.target.value)} />
              <label>Start Date:</label>
              <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
              <label>Start Time:</label>
              <input type="time" value={startTime} onChange={(e) => setStartTime(e.target.value)} />
              <label>End Time:</label>
              <input type="time" value={endTime} onChange={(e) => setEndTime(e.target.value)} />
              <button type="submit">Save</button>
              <button type="button" onClick={closeEditModal}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Calendar;
