import React, { useEffect } from 'react';
import { useAuthContext } from "../hooks/useAuthContext";
import { useClassContext } from "../hooks/useClassContext"; // Use the class context to get classroom data

const Account = () => {
    const { user } = useAuthContext();
    const { classroom } = useClassContext(); // Get classroom from context

    // Add a fallback in case user or classroom is null
    if (!user) {
        return <div>Loading...</div>; // Or a better fallback message
    }

    return (
        <div className="account">
            <h1>Account Info</h1>
            <p>Account Type: {user.role}</p>
            <p>Name: {user.name}</p>
            <p>Email: {user.email}</p>
            {user.role === "student" && (
                <p>Teacher's Email: {classroom?.email ?? "email not provided"}</p>
            )}
            <p>
                Class Name: {classroom?.classroomName ?? "Classroom not created yet"}
            </p>
            <p>Class Code: {user.code ?? "Class Code not created yet"}</p>
        </div>
    );
};

export default Account;
