import { useState } from "react"
import { useSignup } from "../hooks/useSignup"

const Signup = () => {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const {signup, error, isLoading} = useSignup()
  const [selectedRole, setSelectedRole] = useState('student');

  const handleSubmit = async (e) => {
    e.preventDefault()

    await signup(email, password)
  }

  const handleButtonClick = (buttonName) => {
      setSelectedRole(buttonName === selectedRole ? null : buttonName);
  };

  return (
    <form className="signup" onSubmit={handleSubmit}>
      <h3>Sign Up</h3>
      
      {selectedRole === 'student' && (
            <>
                <input
                    type="text"
                    name="name"
                    placeholder="Student Name"
                    required
                />
            </>
        )}
        {selectedRole === 'teacher' && (
            <>
                <input
                    type="text"
                    name="className"
                    placeholder="Class Name"
                    required
                />
                <input
                    type="text"
                    name="name"
                    placeholder="Name"
                    required
                />
            </>
        )}
      <label>Email address:</label>
      <input 
        type="email" 
        onChange={(e) => setEmail(e.target.value)} 
        value={email} 
      />
      <label>Password:</label>
      <input 
        type="password" 
        onChange={(e) => setPassword(e.target.value)} 
        value={password} 
      />
      
      <div className="RoleButtonsContainer">
          <button 
              className={`role-button ${selectedRole === 'student' ? 'active' : ''}`} 
              type="button" // Prevent form submission
              onClick={() => handleButtonClick('student')}
          >
              I'm a Student/Parent
          </button>
          <button 
              className={`role-button ${selectedRole === 'teacher' ? 'active' : ''}`} 
              type="button" // Prevent form submission
              onClick={() => handleButtonClick('teacher')}
          >
              I'm a Teacher
          </button>
      </div>
      <button disabled={isLoading}>Sign up</button>
      {error && <div className="error">{error}</div>}
    </form>
  )
}

export default Signup
