import React, { useEffect, useState } from 'react';
//import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { useEventsContext } from '../hooks/useEventsContext'; // Import the events context

// components
import CalendarNav from '../components/CalendarNav';
import EventsDetails from '../components/EventsDetails';
//import WorkoutForm from '../components/WorkoutForm';
import Calendar from '../components/Calendar';
import EventForm from '../components/CalendarForm';

const Home = () => {
   // const { workouts, dispatch } = useWorkoutsContext();
    const { user } = useAuthContext();
    const { events, dispatch: eventDispatch } = useEventsContext(); // Get events and dispatch

    const [startDate, setStartDate] = useState("2024-10-04");

    useEffect(() => {
       /* const fetchWorkouts = async () => {
            const response = await fetch('/api/workouts', {
                headers: { 'Authorization': `Bearer ${user.token}` },
            });
            const json = await response.json();

            if (response.ok) {
                dispatch({ type: 'SET_WORKOUTS', payload: json });
            }
        };
        */
        const fetchEvents = async () => {
            const response = await fetch('/api/events', {
                headers: { 'Authorization': `Bearer ${user.token}` },
            });
            const json = await response.json();
        
            if (response.ok) {
                console.log("Fetched events:", json);
                // Map the fetched events to the format expected by the Calendar
                const mappedEvents = json.map(event => ({
                    id: event._id, // Assuming the ID is in this property
                    text: event.text,
                    start: event.startDate, // Ensure this maps correctly
                    end: event.endDate,     // Ensure this maps correctly
                    backColor: event.backColor, // Optional, if you have this property
                    participants: event.participants // Optional, if you have this property
                }));
        
                // Validate that all events have required properties
                if (mappedEvents.every(event => event.start && event.end)) {
                    eventDispatch({ type: 'SET_EVENTS', payload: mappedEvents }); // Dispatch the mapped events
                } else {
                    console.error("Some events are missing required properties:", mappedEvents);
                }
            } else {
                console.error("Failed to fetch events:", json);
            }
        };
        
        
        console.log('Fetched events:', events);


        if (user) {
            fetchEvents(); // Fetch events when user is present
        }
    }, [eventDispatch, user]);

    return (
        <div className="home">
            <div>
                <CalendarNav setStartDate={setStartDate} />
            </div>
            <div className='calendar'>
                <Calendar startDate={startDate} events={events} /> {/* Pass events to Calendar */}
            </div>
            <EventForm />
        </div>
    );
}

export default Home;
