import React from 'react';
import { DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrashAlt } from '@fortawesome/free-solid-svg-icons'; // Import trash can icon

const Calendar = ({ startDate, events, onDeleteEvent }) => {
  const formattedEvents = events.map(event => ({
    id: event.id,
    text: (
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <span>{event.text}</span>
        {/* Add the trash icon as a button */}
        <FontAwesomeIcon 
          icon={faTrashAlt} 
          style={{ cursor: 'pointer', color: '#e7195a', marginLeft: '10px' }} 
          onClick={() => handleDelete(event.id)} 
        />
      </div>
    ),
    start: new Date(event.start).toISOString(),
    end: new Date(event.end).toISOString(),
    backColor: event.backColor || "#ffffff",
  }));

  const config = {
    viewType: "Week",
    durationBarVisible: false,
    startDate: startDate,
    events: formattedEvents,
    onEventClick: async (args) => {
      if (window.confirm(`Do you want to delete the event "${args.e.text()}"?`)) {
        await onDeleteEvent(args.e.id());
      }
    }
  };

  const handleDelete = (eventId) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      onDeleteEvent(eventId);
    }
  };

  return (
    <div>
      <DayPilotCalendar {...config} />
    </div>
  );
};

export default Calendar;
