import React, { useEffect } from 'react';
import { useAuthContext } from "../hooks/useAuthContext";
import { useClassContext } from "../hooks/useClassContext";  // Import useClassContext

const Account = () => {
    const { user } = useAuthContext();
    const { classes } = useClassContext();  // Access classes from context

    useEffect(() => {
        // Optionally log to see when the context changes
        console.log("Classes updated:", classes);
    }, [classes]);  // This will trigger whenever the context changes

    return (
        <div className="account">
            <h1>Account Info</h1>
            <p>Account Type: {user.role}</p>
            <p>Name: {user.name}</p>
            <p>Email: {user.email}</p>
            {user.role === "student" && (
                <p>Teacher's Email: {classes?.email ?? "email not provided"} </p>  
            )}
            <p>
                Class Name: {classes?.classroomName ?? "Classroom not created yet"}
            </p>
            <p>Class Code: {user.code || "Code not created yet"}</p>
        </div>
    );
};

export default Account;
