import React, { useEffect, useState } from 'react';
//import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { useEventsContext } from '../hooks/useEventsContext'; // Import the events context

// components
import CalendarNav from '../components/CalendarNav';
import EventsDetails from '../components/EventsDetails';
//import WorkoutForm from '../components/WorkoutForm';
import Calendar from '../components/Calendar';
import EventForm from '../components/CalendarForm';

const Home = () => {
   // const { workouts, dispatch } = useWorkoutsContext();
    const { user } = useAuthContext();
    const { events, dispatch: eventDispatch } = useEventsContext(); // Get events and dispatch

    const [startDate, setStartDate] = useState(args.day);

    useEffect(() => {
       /* const fetchWorkouts = async () => {
            const response = await fetch('/api/workouts', {
                headers: { 'Authorization': `Bearer ${user.token}` },
            });
            const json = await response.json();

            if (response.ok) {
                dispatch({ type: 'SET_WORKOUTS', payload: json });
            }
        };
        */
        const fetchEvents = async () => {
            const response = await fetch('/api/events', {
                headers: { 'Authorization': `Bearer ${user.token}` },
            });
            const json = await response.json();
        
            if (response.ok) {
                //console.log("Fetched events:", json); // Log the raw response
                const mappedEvents = json.map(event => ({
                    id: event._id,            // Use the correct id
                    text: event.text,         // Directly use the fetched text
                    start: event.start,       // Directly use the fetched start
                    end: event.end,           // Directly use the fetched end
                    backColor: event.backColor || "#ffffff", // Fallback if backColor isn't provided
                    participants: event.participants || 0,    // Fallback if participants isn't provided
                }));
        
                console.log("Mapped events:", mappedEvents); // Log the mapped events
                eventDispatch({ type: 'SET_EVENTS', payload: mappedEvents }); // Dispatch the mapped events
            } else {
                console.error("Error fetching events:", json);
            }
        };
        
        
        console.log('Fetched events:', events);


        if (user) {
            fetchEvents(); // Fetch events when user is present
        }
    }, [eventDispatch, user]);

    return (
        <div className="home">
            <div>
                <CalendarNav setStartDate={setStartDate} />
            </div>
            <div className='calendar'>
                <Calendar startDate={startDate} events={events} /> {/* Pass events to Calendar */}
            </div>
            <EventForm />
        </div>
    );
}

export default Home;
